DROP TABLE IF EXISTS Villain;
DROP SEQUENCE IF EXISTS villain_seq;

CREATE SEQUENCE villain_seq START 1 INCREMENT 50;

CREATE TABLE Villain (
  id int8 NOT NULL,
  level int4 NOT NULL,
  name VARCHAR(50) NOT NULL,
  otherName VARCHAR(255),
  picture VARCHAR(255),
  powers TEXT,
  PRIMARY KEY (id)
);

